import { React, useState, useEffect } from 'react';
import RedBox from './RedBox';
import GrabStocks from './GrabStocks';
import { useStockEvaluation } from './context/StockEvaluationContext';
import UpdateNetworthBox from './UpdateNetworthBox';
import FinacneForm from './FinanceForm';
import { updateNetworth } from '../javascript/updateNetworth';
import { pullNetworth } from '../javascript/pullNetworth';
function NetWorth() {
  // const { header } = props
  const vooStockAmount = 2.91097;
  const googlStockAmount = 5;
  const pltrStockAmount = 15;
  const { stockEvaluation } = useStockEvaluation();
  const [totalNetworth, setTotalNetworth ] = useState(0); 
  const [accountBalence, setAccountBalence ] = useState(0); 


  const handelFinacneFormSubmit = (selectedOption, currencyAmount) => {
    // need to check for selected option 
    setAccountBalence(updateNetworth(selectedOption, currencyAmount))
    setTotalNetworth( stockEvaluation + accountBalence)

  };

  useEffect(() => { 
    setTotalNetworth(stockEvaluation) });

  return (
    <>
      <div className="net-worth">
        <GrabStocks
          vooStockAmount={vooStockAmount}
          googlStockAmount={googlStockAmount}
          pltrStockAmount={pltrStockAmount}
        />
        <RedBox leftText="Net Worth:" rightText={totalNetworth === 0 ? ('Calculating...') : (`$${totalNetworth}`)} />
        <h1 style = {{ color: '#326771', fontSize: '54px' , textAlign: 'left', paddingLeft: '15px'}}>Welcome Bennett!</h1>

        <div>
          <UpdateNetworthBox></UpdateNetworthBox>
          <FinacneForm onSubmit={handelFinacneFormSubmit}></FinacneForm>


        </div>
      </div>
    </>
  );
}

export default NetWorth;
