import React, { useEffect, useState } from 'react';
import { Redirect } from 'react-router-dom';

const LoginPage = ({ onLogin }) => {
  const [password, setPassword] = useState('');
  const [redirectToNetWorth, setRedirectToNetWorth] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password === '1') {
      onLogin();
      setRedirectToNetWorth(true); 
      console.log(redirectToNetWorth);
    }
  };
  const onChangeText = (e) => {
    (e) => setPassword(e.target.value)
    if (password === '1'){
        setRedirectToNetWorth(true)
    }
  }

  // Redirect to the NetWorth page if the flag is true

  return (
    <div>
      <h2 style={{padding:'20px'}}>Please Enter a password to access this page</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={onChangeText}
        />
        <button style={{marginLeft: '10px'}} type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
