import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';

const LoginPage = ({ onLogin }) => {
  const [password, setPassword] = useState('');
  const [redirectToNetWorth, setRedirectToNetWorth] = useState(false);

  useEffect(() => {
    if (redirectToNetWorth) {
      // Redirect to the NetWorth page after the state update
      window.location.href = '/networth';
    }
  }, [redirectToNetWorth]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (password === '1') {
      onLogin();
      setRedirectToNetWorth(true);
    }
  };

  return (
    <div>
      <h2 style={{ padding: '20px' }}>
        Please Enter a password to access this page
      </h2>
      <form onSubmit={handleSubmit}>
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button style={{ marginLeft: '10px' }} type="submit">
          Login
        </button>
      </form>
    </div>
  );
};

export default LoginPage;
