import { React, useState, useEffect } from 'react';
import RedBox from './RedBox';
import GrabStocks from './GrabStocks';
import { useStockEvaluation } from './context/StockEvaluationContext';
import UpdateNetworthBox from './UpdateNetworthBox';
import FinacneForm from './FinanceForm';
function NetWorth() {
  // const { header } = props
  const vooStockAmount = 2.91097;
  const googlStockAmount = 5;
  const pltrStockAmount = 15;
  const { stockEvaluation } = useStockEvaluation();
  const [output, setOutput] = useState('');
  const [totalNetworth, setTotalNetworth ] = useState(0); 

  const executePythonScript = async (selectedOption, currencyAmount) => {
    const pythonScript = '/Users/benitochall/Documents/personalDevelopment/aboutMeProject/aboutMe/backend/updateFinances/updateFinances.py';
    const args = `${selectedOption} ${currencyAmount}`;
    // Notes to futures self, I had to change the executable permissions of the pull stocks data
    // as well as make sure the path was correct

    try {
      const response = await fetch('http://localhost:3001/executePython', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ pythonScript, args }),
      });

      if (!response.ok) {
        throw new Error('Failed to execute Python script');
      }

      const data = await response.json();
      setOutput(data.output);
    } catch (error) {
      console.error(error);
      setOutput('Error occurred while executing Python script');
    }
  };

  const handelFinacneFormSubmit = (selectedOption, currencyAmount) => {
    // need to check for selected option 
    let database = ''
    if (selectedOption === 'Paycheck'){
      database = 'earnings'
    }
    else if (selectedOption === 'Gift'){
      database = 'gifts'
    }

    else if (selectedOption === 'Education'){
      database = 'education'
    }

    else if (selectedOption === 'Rent'){
      database = 'rent'
    }

    else if (selectedOption === 'Food'){
      database = 'food'
    }

    else if (selectedOption === 'Clothing'){
      database = 'clothing'
    }

    else if (selectedOption === 'Travel'){
      database = 'travel'
    }

    else if (selectedOption === 'Misc'){
      database = 'misc'
    }

    else if (selectedOption === 'Investment'){
      database = 'investment'
    }

    else if (selectedOption === 'Add to Investment Account'){
      database = 'amountInvested'
    }

    executePythonScript(database, currencyAmount); // this will update our output value
    // now we need to add the update to the total networth
    setTotalNetworth( stockEvaluation + Number(output))

  };

  useEffect(() => { executePythonScript(0,0); });

  return (
    <>
      <div className="net-worth">
        <GrabStocks
          vooStockAmount={vooStockAmount}
          googlStockAmount={googlStockAmount}
          pltrStockAmount={pltrStockAmount}
        />
        <RedBox leftText="Net Worth:" rightText={totalNetworth === 0 ? ('Calculating...') : (`$${totalNetworth}`)} />
        <h1 style = {{ color: '#326771', fontSize: '54px' , textAlign: 'left', paddingLeft: '15px'}}>Welcome Bennett!</h1>

        <div>
          <UpdateNetworthBox></UpdateNetworthBox>
          <FinacneForm onSubmit={handelFinacneFormSubmit}></FinacneForm>


        </div>
      </div>
    </>
  );
}

export default NetWorth;
