import React, { useState } from 'react';

/* CSS for horizontal form layout */
const formContainerStyle = {
    display: 'flex',
    flexDirection: 'row',
    gap: '20px',
  };

  const labelStyle = {
    display: 'flex',
    flexDirection: 'column',
  };

  const inputStyle = {
    width: '100px',
  };

function MyForm() {
  // State for dropdown and currency input
  const [selectedOption, setSelectedOption] = useState('');
  const [currencyAmount, setCurrencyAmount] = useState('');

  // Dropdown options
  const dropdownOptions = ['Paycheck', 'Gift', 'Gas', 'Education', 'Rent', ];

  const handleDropdownChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleCurrencyInputChange = (event) => {
    setCurrencyAmount(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
  };

  return (
    <form style = {formContainerStyle} onSubmit={handleSubmit} className="form-container">
      <label style = {labelStyle}>
        Dropdown:
        <select value={selectedOption} onChange={handleDropdownChange}>
          <option value="">Select Purchase/Payment</option>
          {dropdownOptions.map((option) => (
            <option key={option} value={option}>
              {option}
            </option>
          ))}
        </select>
      </label>
      <label style= {labelStyle}>
        Amount:
        <input
          type="number"
          value={currencyAmount}
          onChange={handleCurrencyInputChange}
          placeholder="Enter amount"
          style = {inputStyle}
        />
      </label>
      <button type="submit">Submit</button>
    </form>
  );
}

export default MyForm;
