import React, { useRef, useEffect, useState } from 'react';

const VideoPreview = ({ src, duration }) => {
  const videoRef = useRef(null);
  const [userInteracted, setUserInteracted] = useState(false);

  useEffect(() => {
    const video = videoRef.current;

    const playPreview = () => {
      if (video) {
        video.currentTime = 0; // Start from the beginning
        video.play();
      }
    };

    const stopPreview = () => {
      if (video) {
        video.pause();
      }
    };

    if (userInteracted) {
      playPreview();
    }

    return () => {
      stopPreview();
    };
  }, [src, duration, userInteracted]);

  useEffect(() => {
    const handleMouseMove = () => {
      setUserInteracted(true);
      // Remove the event listener after user interaction if you only want it to play once
      document.removeEventListener('mousemove', handleMouseMove);
    };

    // Attach event listener to document for mousemove
    document.addEventListener('mousemove', handleMouseMove);

    // Cleanup function to remove the event listener when component unmounts
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <video ref={videoRef} width="344" height="150" loop>
      <source src={src} type="video/mp4" />
      Your browser does not support the video tag.
    </video>
  );
};

export default VideoPreview;
